class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        ans = [float("inf")]*n
        for i in range(n):
            if nums[i] == 0:
                ans[i] = 0
                continue
            p = 1
            for j in range(len(queries)):
                if queries[j][0] <= i <= queries[j][1]:
                    p |= (p << queries[j][2])
                if ((p >> nums[i]) & 1):
                    ans[i] = j+1
                    break
        mx = max(ans)
        return mx if mx != float("inf") else -1

