class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int n = nums.size();
        int ans = 0;
        for(int i=0;i<n;++i){
            if(nums[i] == 0)continue;
            memset(visited,0,sizeof(visited));
            int top = 0;
            sta[top++] = nums[i];
            f[i] = -1;
            visited[nums[i]] = true;
            for(int j=0;j<m;++j){
                if(queries[j][0] > i || queries[j][1] < i)continue;
                int topn = top;
                for(int k=0;k<topn;++k){
                    if(sta[k] >= queries[j][2]){
                        int x = sta[k] - queries[j][2];
                        if(!visited[x]){
                            visited[x] = true;
                            sta[top++] = x;
                        }
                    }
                }
                if(visited[0]){
                    f[i] = j+1;
                    break;
                }
            }
            if(f[i] == -1){
                ans = -1;
                break;
            }else ans = max(ans, f[i]);
        }
        return ans;
    }
};

