class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        ans = 0
        memo = [[] for _ in range(n)]
        for start, end, val in queries:
            for i in range(n):
                memo[i].append(val if start <= i <= end else 0)
        for idx, me in enumerate(memo):
            num = nums[idx]
            if num == 0:
                continue
            target = 1 << num
            mask = target * 2 - 1
            tmp = 1
            for i, val in enumerate(me):
                tmp = ((tmp << val) | tmp) & mask
                if tmp & target != 0:
                    ans = max(ans, i + 1)
                    break
            else:
                return -1
        return ans

