class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        n = nums.length;
        a = nums;
        g = queries;
        int l = -1, r = queries.length + 1;
        while (l + 1 < r) {
            int m = l + (r - l) / 2;
            if (f(m)) {
                r = m;
            } else {
                l = m;
            }
        }
        return r <= queries.length ? r : -1;
    }

    int n, a[], g[][];

    boolean f(int m) {
        for (int i = 0; i < n; i++) {
            int s = a[i];
            boolean[] dp = new boolean[s + 1];
            dp[0] = true;
            for (int j = 0; j < m; j++) {
                if (in(i, g[j])) {
                    int v= g[j][2];
                    for (int k = s; k >= v; k--  ) {
                        dp[k] |= dp[k - v];
                    }
                }
            }
            if (!dp[s]) {
                return false;
            }

        }
        return true;
    }

    boolean in(int x, int[] e) {
        return x >= e[0] && x <= e[1];
    }
}

