class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        {
            int mx = 0;
            for (auto x : nums) {
                mx = max(mx, x);
            }
            if (mx == 0) {
                return 0;
            }
        }
        int n = (int) nums.size();
        int m = (int) queries.size();

        using BIT = bitset<1024>;


        vector<int> A(n, m + 1);

        auto calc = [&](int idx) {
            BIT dp;
            dp.set(0);

            int T = nums[idx];

            for (int i = 0; i < m; ++i) {
                auto& Q = queries[i];
                int l = Q[0], r = Q[1], v = Q[2];
                if (l <= idx && idx <= r) {
                    dp |= dp << v;
                }
                if (dp.test(T)) {
                    return i + 1;
                }
            }


            return m + 1;
        };

        for (int i = 0; i < n; ++i) {
            A[i] = calc(i);
        }

        int ans = -1;
        for (int i = 0; i < n; ++i) {
            ans = max(ans, A[i]);
        }
        if (ans > m) {
            return -1;
        }
        return ans;
    }
};

