class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> coverage(n);

        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            for (int i = l; i <= r; ++i) {
                coverage[i].push_back(j);
            }
        }

        int minK = 0;
        bool possible = true;

        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }

            unordered_map<int, int> dp;
            dp[0] = -1;
            vector<int>& cov = coverage[i];
            bool found = false;

            for (int j : cov) {
                if (dp.find(target) != dp.end()) {
                    found = true;
                    break;
                }

                int val = queries[j][2];
                unordered_map<int, int> tmp;

                for (auto& entry : dp) {
                    int s = entry.first;
                    int current_max_j = entry.second;
                    int new_s = s + val;

                    if (new_s > target) {
                        continue;
                    }

                    int new_max_j = max(current_max_j, j);
                    auto it = tmp.find(new_s);
                    if (it == tmp.end() || new_max_j < it->second) {
                        tmp[new_s] = new_max_j;
                    }
                }

                for (auto& entry : tmp) {
                    int s = entry.first;
                    int new_max_j = entry.second;
                    auto it = dp.find(s);
                    if (it == dp.end() || new_max_j < it->second) {
                        dp[s] = new_max_j;
                    }
                }
            }

            if (dp.find(target) == dp.end()) {
                possible = false;
                break;
            }

            int k_i = dp[target] + 1;
            if (k_i > minK) {
                minK = k_i;
            }
        }

        if (!possible) {
            return {-1};
        }

        for (int i = 0; i < n; ++i) {
            if (nums[i] != 0) {
                continue;
            }
        }

        return {minK};
    }
};

