class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
                int n=nums.length;
        int[][]dp=new int[n][];
        for(int i=0;i<n;i++){
            dp[i]=new int[nums[i]+1];
            Arrays.fill(dp[i],Integer.MAX_VALUE);
            dp[i][0]=-1;
        }
        for(int i=0;i<queries.length;i++){
            int val=queries[i][2];
            for(int j=queries[i][0];j<=queries[i][1];j++){
                int[]cur=dp[j];
                for(int k=cur.length-1;k>=val;k--){
                    if(cur[k-val]!=Integer.MAX_VALUE){
                        cur[k]=Math.min(cur[k],i);
                    }
                }
            }
        }
        int need=-1;
        for(int i=0;i<n;i++){
            need=Math.max(need,dp[i][dp[i].length-1]);
        }
        return need==Integer.MAX_VALUE?-1:(need+1);
    }
}

