class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int l = 0,r = m+1;
        auto cal = [&](int md) {
          vi pos[15];
          repi(i,1,md) {
            int l = queries[i-1][0], r = queries[i-1][1], x = queries[i-1][2];
            repi(j,l,r) {
              pos[j].pb(x);
            }
          }
          repi(p,0,n-1) {
            bool dp[1005] = {1};
            for(auto x:pos[p]) {
              peri(j,nums[p],x) {
                dp[j] |=dp[j-x];
              }
            }
            if (!dp[nums[p]]) {
              return false;
            }
          }
          return true;
        };
        while(l<r){
            int md = (l+r)>>1;
            if (cal(md)) {
              r = md;
            } else {
              l = md + 1;
            }
        }
        return r == m + 1 ? -1 : r;
    }
};

