                for (int k = 0; k <= nums[i] - queries[j][2]; k++) {
                    if (f[k + queries[j][2]]) {
                        f[k] = true;
                    }
                }
                if (f[0]) {
                    is_acc = true;
                    v = j + 1;
                    break;
                }
            }
            if (is_acc) {
                res = max(res, v);
            } else {
                return -1;
            }
        }
        return res;
    }
};

