    {
        int n = nums.size(), q = queries.size();
        int ans = 0;
        bool zero = true;
        for (auto x : nums)
            if (x)
            {
                zero = false;
                break;
            }
        if (zero)
            return 0;
        for (int t = 0; t < n; t++)
        {
            vector<int> f(nums[t] + 1, 0);
            f[0] = 1;
            int cnt = 0;
            for (auto x : queries)
            {
                if (x[0] <= t && x[1] >= t)
                    for (int j = nums[t]; j >= x[2]; j--)
                        f[j] |= f[j - x[2]];
                cnt++;
                if (f[nums[t]])
                {
                    ans = max(ans, cnt);
                    break;
                }
            }
            if (!f[nums[t]])
                return -1;
        }
        return ans;
    }
};

