class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int ans = 0, can = 0;
        for(int i = 0; i < n; i++) {
            bitset<1001> dp;
            dp[0] = 1;
            if(dp[nums[i]]) {
                ans = max(ans, 0);
                can++;
                continue;
