            for(int j = 0; j < q; j++) {
                int l, r, val;
                l = queries[j][0];
                r = queries[j][1];
                val = queries[j][2];
                if(i >= l && i <= r) {
                    dp |= dp << val;
                }
                if(dp[nums[i]]) {
                    ans = max(ans, j + 1);
                    can++;
                    break;
                }
            }
        }
        return can == n ? ans : -1;
    }
};

