class Solution {
public:
	int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
		int n = a.size();
		int m = queries.size();

		if (ranges::count(a, 0) == n) {
			return 0;
		}

		auto check = [&](int lim) -> bool {
			vector<vector<int>> nums(n);
			for (int j = 0; j <= lim; j ++) {
				auto& q = queries[j];
				int l = q[0];
				int r = q[1];
				int v = q[2];
				for (int i = l; i <= r; i ++) {
					nums[i].push_back(v);
				}
			}

			for (int i = 0; i < n; i ++) {
				bitset<1001> dp;
				dp[0] = 1;
				for (int x : nums[i]) {
					dp |= dp << x;
				}
				if (dp[a[i]] == 0) {
					return false;
				}
			}

			return true;
		};

		int l = 0, r = m - 1, ans = -1;
		while (l <= r) {
			int mid = l + r >> 1;
			check(mid) ? r = mid - 1, ans = mid : l = mid + 1;
		}
		if (ans != -1) {
			ans ++;
		}
		return ans;
	}
};

