class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        m=len(queries)
        if sum(nums)==0:
            return 0
        res=[]
        for k in range(n):
            x=nums[k]
            if nums[k]==0:
                res.append(0)
                continue
            t=[]
            for j,(l,r,v) in enumerate(queries):
