                    t.append((v,j))
            if not t:
                return -1
            m=len(t)
            # print(k,x,t)
            @cache
            def dfs(i,val):
                if i==m:
                    return inf
                if val-t[i][0] == 0:
                    return i
                if val<0:
                    return inf
                return min(dfs(i+1,val-t[i][0]),dfs(i+1,val))
            ans = dfs(0,x)

            if ans==inf:
                return -1
            res.append(t[ans][1])
            dfs.cache_clear()
        # print(res)
        return max(res)+1

