class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        tmp = [[] for _ in range(len(nums))]
        for i, (left, right, val) in enumerate(queries):
            for j in range(left, right+1):
                tmp[j].append((i, val)) #idx, diff

        @cache
        def dfs(num, cur, i):
            nonlocal this
            if num <= 0: return
            if cur >= len(tmp[i]): return

            if num-tmp[i][cur][1] == 0:
                this = min(this, tmp[i][cur][0])
                return
            dfs(num, cur+1, i)
            dfs(num-tmp[i][cur][1], cur+1, i)

        res = -inf
        for i in range(len(tmp)):
            this = inf
            if nums[i] != 0:
                dfs(nums[i], 0, i)
            else:
                this = -1

            if this == inf: return -1
            this += 1
            res = max(res, this)
