import java.util.*;
import java.math.BigInteger;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        boolean allZero = true;
        for (int num : nums) {
            if (num > 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        List<Integer> nonZeroIndices = new ArrayList<>();
        for (int j = 0; j < n; j++) {
            if (nums[j] > 0) {
                nonZeroIndices.add(j);
            }
        }

        int maxv = 1000;
        BigInteger mask = BigInteger.ONE.shiftLeft(maxv + 1).subtract(BigInteger.ONE);  
        BigInteger[] aux = new BigInteger[n]; 
        Arrays.fill(aux, BigInteger.ONE);  

        for (int i = 0; i < queries.length; i++) {
            int left = queries[i][0];
            int right = queries[i][1];
            int val = queries[i][2];

            for (int j = left; j <= right; j++) {
                if (nums[j] > 0) {  
                    aux[j] = aux[j].or(aux[j].shiftLeft(val));
                    aux[j] = aux[j].and(mask);
                }
            }

            boolean flag = true;
            for (int j : nonZeroIndices) {
                if (!aux[j].testBit(nums[j])) {  
                    flag = false;
                    break;
                }
            }

            if (flag) {
                return i + 1;  
            }
        }
        return -1;
    }
}

