    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = -1, n = nums.size(), m = queries.size(), cnt = 0;
        for (int i = 0; i < n; i++) {
            vector<int> dp(nums[i] + 1, 0);
            dp[0] = 1;
            if (nums[i] == 0) {
                res = max(res, 0);
                cnt++;
                continue;
