class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        int ans = -1;
        for (int i = 0; i < n; i++) {
            ans = max(ans, getCost(i, nums[i], queries));
        }

        if (ans == INT_MAX) return -1;
        else return ans;
    }
    int getCost(int index, int target, const vector<vector<int>> &queries) {
        if (target == 0) return 0;
        bitset<1001> bs;
        bs.set(0);
        for (int qi = 0; qi < queries.size(); qi++) {
            int l = queries[qi][0];
            int r = queries[qi][1];
            int v = queries[qi][2];
            if (l > index || r < index) continue;
            bs |= (bs << v);
            if (bs.test(target)) return qi + 1;
        }
        return INT_MAX;
    }
};

