class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        cnt_zero = nums.count(0)
        if cnt_zero == n:
            return 0

        def helper(idx):
            bit = 1 << nums[idx]
            cur = 1
            for i, (l, r, val) in enumerate(queries, 1):
                if not (l <= idx <= r):
                    continue
                cur |= cur << val
                if cur & bit:
                    return i
            return 10 ** 9

        res = [helper(i) for i in range(n)]
        k = max(res)
        return k if k != 10 ** 9 else -1

