class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        auto check = [&](int k) -> bool {
            for (int j = 0; j < n; j++) {
                bitset<1100> dp;
                dp[0] = 1;
                for (int i = 0; i < k; i++) {
                    int li = queries[i][0], ri = queries[i][1], val = queries[i][2];
                    if (li <= j && j <= ri) {
                        dp |= (dp << val);
                    }
                }
                if (!dp[nums[j]])
                    return false;
            }
            return true;
        };

        int l = 0, r = m;
        while (l < r) {
            int mid = (l + r) / 2;
            if (check(mid)) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (check(l)) return l;
        return -1;
    }
};

