class Solution {
    int dp[1005][1005] = {0};
    int cur[1005][1005] = {0};
    int cnt[1005] = {0};
    bool dfs(int t,int id,int target){
        if(target == 0){
            return true;
        }
        //cout<<t<<" "<<id<<endl;
        int val = cur[t][id];
        if(id == 0){
            return val == target;
        }

        if(dp[id][target] != -1){
            return dp[id][target] == 1;
        }
        bool ans = dfs(t,id-1,target);
        if(target >= val)
            ans |= dfs(t,id-1,target-val);
        if(ans){
            dp[id][target] = 1;
        }else{
            dp[id][target] = 0;
        }
        return ans;
    }
    bool f(int id,int target){
        int n = cnt[id];
        for(int i = 0;i<n;i++){
            for(int j = 0;j<=target;j++){
                dp[i][j] = -1;
            }
        }
        if(n == 0){
            return target == 0;
        }
        return dfs(id,n-1,target);
    }
    bool func(int t,vector<int>& nums, vector<vector<int>>& q){
        int n = nums.size();
        for(int i = 0;i<n;i++){
            cnt[i] = 0;
        }
        for(int i = 0;i<t;i++){
            for(int j = q[i][0];j<=q[i][1];j++){
                int id = cnt[j];
                cur[j][id] = q[i][2];
                cnt[j]++;
            }
        }
        for(int i = 0;i<n;i++){
            if(!f(i,nums[i])){
                return false;
            }
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int m = q.size();
        if(!func(m,nums,q)){
            return -1;
        }
        int l = 0,r = m;
        while(l<r){
            int mid = (l+r)>>1;
            if(func(mid,nums,q)){
                r = mid;
            }else{
                l = mid+1;
            }
        }
        return l;
    }
};

