def bs(a, x):
    l, r = 0, len(a) - 1
    while l <= r:
        m = (l + r) // 2
        if a[m] == x:
            return m
        if a[m] < x:
            l = m + 1
        else:
            r = m - 1
    return -1

def st(a):
    n = len(a)
    s = 1
    while s < n:
        s *= 2
    t = [0] * (2 * s)
    for i in range(n):
        t[s + i] = a[i]
    for i in range(s - 1, 0, -1):
        t[i] = t[2 * i] + t[2 * i + 1]
    def q(l, r):
        l += s; r += s; tot = 0
        while l <= r:
            if l & 1:
                tot += t[l]
                l += 1
            if not (r & 1):
                tot += t[r]
                r -= 1
            l //= 2; r //= 2
        return tot
    return t, q

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        dp = [1] * len(nums)
        c = [(1 << (num + 1)) - 1 for num in nums]

        if all(x == 0 for x in nums):
            return 0

        for k, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                dp[i] |= (dp[i] << val)
                dp[i] &= c[i]
            if all(dp[i] & (1 << nums[i]) for i in range(len(nums))):
                return k + 1
        return -1

