class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        covered = [[] for _ in range(n)]
        for j in range(m):
            l, r, val = queries[j]
            for i in range(n):
                if l <= i <= r:
                    covered[i].append((j, val))

        max_k = 0
        for i in range(n):
            num = nums[i]
            if num == 0:
                continue 

            queries_i = covered[i]
            if not queries_i:
                return -1

            dp = {0: -1}
            for (j_index, val) in queries_i:
                new_dp = {}
                for s in dp:
                    current_max = dp[s]
                    if s in new_dp:
                        if current_max < new_dp[s]:
                            new_dp[s] = current_max
                    else:
                        new_dp[s] = current_max
                    new_s = s + val
                    if new_s > num:
                        continue
                    new_max = max(current_max, j_index)
                    if new_s in new_dp:
                        if new_max < new_dp[new_s]:
                            new_dp[new_s] = new_max
                    else:
                        new_dp[new_s] = new_max
                dp = new_dp

            if num not in dp:
                return -1
            k_i = dp[num] + 1 
            if k_i > max_k:
                max_k = k_i
        zero_count = sum(1 for x in nums if x == 0)
        if zero_count == n:
            return 0
        if max_k > m:
            return -1
        return max_k if max_k != 0 else -1

