class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), m = q.size();
        if(accumulate(a.begin(), a.end(), 0) == 0) return 0;
        auto check = [&](int m) {
            vector<vector<int>> op(n);
            for(int i = 0; i < m; i ++) {
                int l = q[i][0], r = q[i][1], val = q[i][2];
                for(int k = l; k <= r; k ++) op[k].push_back(val);
            }
            for(int i = 0; i < n; i ++) {
                vector<int> f(a[i] + 1, 0);
                f[0] = 1;
                for(int j = 0; j < op[i].size(); j ++) {
                    if(op[i][j] > a[i]) continue;
                    for(int k = a[i]; k >= op[i][j]; k --)
                        f[k] |= f[k - op[i][j]];
                }
                // cout << m << endl;
                // for(int j = 0; j <= a[i]; j ++) cout << f[j] << " "; cout << endl;
                if(f[a[i]] == 0) return false;
            }
            return true;
        };
        if(check(m) == 0) return -1;
        int l = 1, r = m;
        while(l < r) {
            int mid = (l + r) / 2;
            if(check(mid)) r = mid;
            else l = mid + 1;
        }
        return l;
    }
};

