import java.util.*;

class Solution extends U {

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;
        boolean zero = true;
        for (int i : nums) {
            if (i != 0) {
                zero = false;
                break;
            }
        }
        if (zero) {
            return 0;
        }
        int[][] cnt = new int[n][11];
        int l = 0, r = q - 1, x;
        while (l <= r) {
            x = (l + r) >> 1;
            for (int i = 0; i < n; i++) {
                Arrays.fill(cnt[i], 0);
            }
            for (int i = 0; i <= x; i++) {
                int v = queries[i][2];
                for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                    cnt[j][v]++;
                }
            }
            boolean ok = true;
            for (int z = 0; z < n; z++) {
                int s = nums[z];
                List<Integer> list = new ArrayList<>();
                for (int i = 1; i <= 10; i++) {
                    if (cnt[z][i] > 0) {
                        int v = cnt[z][i];
                        for (int t = 1; t <= v; t <<= 1) {
                            list.add(t * i);
                            v -= t;
                        }
                        if (v > 0) {
                            list.add(v * i);
                        }
                    }
                }
                int m = list.size();
                boolean[][] dp = new boolean[m + 1][s + 1];
                dp[m][0] = true;
                for (int i = m - 1; i >= 0; i--) {
                    int v = list.get(i);
                    for (int j = 0; j <= s; j++) {
                        dp[i][j] = dp[i + 1][j] || (j >= v && dp[i + 1][j - v]);
                    }
                }
                if (!dp[0][s]) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                r = x - 1;
            } else {
                l = x + 1;
            }
        }
        r += 2;
        return r > q ? -1 : r;
    }
}

