class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        lo, hi = 0, m + 1
        while lo < hi:
            mid = (lo + hi) // 2
            res = [1] * n
            for j in range(mid):
                l, r, v = queries[j]
                for i in range(l, r + 1):
                    res[i] |= res[i] << v
            ok = True
            for i in range(n):
                if (res[i] >> nums[i]) & 1 == 0:
                    ok = False
                    break
            if ok:
                hi = mid
            else:
                lo = mid + 1
        return lo if lo <= m else -1

