class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        vector<vector<pair<int,int>>>v(n);
        for(int i=0;i<m;++i){
            int l =queries[i][0],r=queries[i][1],val=queries[i][2];
            for(int j=l;j<=r;++j){
                v[j].emplace_back(i,val);
            }
        }

        int mk=-1;
        for(int i=0;i<n;++i){
            int cur =nums[i];
            if(cur==0)continue;
            vector<pair<int,int>> tv=v[i];

            if(tv.empty())return -1;
            int tgt=cur;
            unordered_map<int,int>dp;
            dp[0]=0;

            for(int j=0;j<tv.size();++j){
                int val =tv[j].second;
                unordered_map<int,int>tdp(dp);

                for(auto p :dp){
                    int ns  =p.first+val;
                    if(ns>tgt)continue;
                    int nt = max(p.second,j+1);
                    if(tdp.find(ns)==tdp.end()||nt<tdp[ns])tdp[ns]=nt;
                }

                dp=move(tdp);
            }

            if(dp.find(tgt)==dp.end())return -1;
            int tj=dp[tgt];
            if(tj>tv.size())return -1;
            int li=tv[tj-1].first;
            mk=max(mk,li);
        }

        return mk==-1?0:mk+1;
    }
};

