class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        count = n
        dp_mat = []
        for num in nums:
            dp = [0] * (num + 1)
            dp[num] = 1
            if num == 0:
                count -= 1
            dp_mat.append(dp)

        if count == 0:
            return 0

        for i in range(m):
            left, right, val = queries[i]
            for j in range(left, right + 1):
                num = nums[j]
                if dp_mat[j][0] == 0 and val <= num:
                    for k in range(num - val + 1):
                        if dp_mat[j][k + val] == 1:
                            dp_mat[j][k] = 1
                    if dp_mat[j][0] == 1:
                        count -= 1
            # print(dp_mat)
            if count == 0:
                return i + 1
        return -1

