class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int queriesCount = queries.length;
        int length = nums.length;
        int maxNum = Arrays.stream(nums).max().getAsInt();
        boolean[][][] dp = new boolean[queriesCount + 1][length][maxNum + 1];
        for (int j = 0; j < length; j++) {
            dp[0][j][nums[j]] = true;
        }
        if (allZeroPossible(dp[0])) {
            return 0;
        }
        for (int i = 1; i <= queriesCount; i++) {
            for (int j = 0; j < length; j++) {
                System.arraycopy(dp[i - 1][j], 0, dp[i][j], 0, maxNum + 1);
            }
            int left = queries[i - 1][0], right = queries[i - 1][1], val = queries[i - 1][2];
            for (int j = left; j <= right; j++) {
                for (int k = maxNum - val; k >= 0; k--) {
                    dp[i][j][k] = dp[i][j][k] || dp[i - 1][j][k + val];
                }
            }
            if (allZeroPossible(dp[i])) {
                return i;
            }
        }
        return -1;
    }

    public boolean allZeroPossible(boolean[][] dpCurr) {
        int length = dpCurr.length;
        for (int j = 0; j < length; j++) {
            if (!dpCurr[j][0]) {
                return false;
            }
        }
        return true;
    }
}

