class Solution {
  public:
    int minZeroArray(vector<int> &nums, vector<vector<int>> &queries) {
        int n = (int)nums.size(), m = (int)queries.size();

        int zeros = 0;
        vector<unordered_set<int>> rests(n);
        for (int i = 0; i < n; i++) {
            rests[i].insert(nums[i]);
            if (nums[i] == 0) {
                ++zeros;
            }
        }

        if (zeros == n) {
            return 0;
        }
        for (int j = 0; j < m; j++) {
            auto &query = queries[j];
            auto l = query[0], r = query[1], val = query[2];

            for (int i = l; i <= r; i++) {
                if (rests[i].contains(0)) {
                    continue;
                }
                if (rests[i].contains(val)) {
                    ++zeros;
                    rests[i].insert(0);
                    continue;
                }

                vector<int> temp;
                for (auto x : rests[i]) {
                    if (x > val) {
                        temp.push_back(x - val);
                    }
                }
                for (auto x : temp) {
                    rests[i].insert(x);
                }
            }

            if (zeros == n) {
                return j + 1;
            }
        }
        return -1;
    }
};

