class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def work(k, t):
            if t == 0:
                return 0
            q = {0}
            for i in range(len(queries)):
                l, r, v = queries[i]
                if l <= k <= r:
                    w = set([x + v for x in q  if x + v <= t])
                    q = w | q
                if t in q:
                    return i + 1
            return -1

        ret = -1
        for i in range(len(nums)):
            z = work(i, nums[i])
            # print(i, nums[i], z)
            if z != -1:
                ret = max(ret, z)
            else:
                return -1
        return ret

