class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int[] times = new int[n];

        for (int i = 0; i < n; i++) {
            int time = deal(i, nums[i], queries);

            if (time == -1) {
                return -1;
            }
            times[i] = time;
        }

        int result = times[0];
        for (int i = 1; i < n; i++) {
            result = Math.max(result, times[i]);
        }

        return result;
    }

    private int deal(int pos, int value, int[][] queries) {
        if (value == 0) {
            return 0;
        }
        HashSet<Integer> set = new HashSet<>();
        set.add(value);

        for (int i = 0; i < queries.length; i++) {
            int[] query = queries[i];

            int l = query[0];
            int r = query[1];
            int v = query[2];

            if (l <= pos && pos <= r) {
                if (set.contains(v)) {
                    return i + 1;
                }

                HashSet<Integer> temp = new HashSet<>();
                for (int x: set) {
                    temp.add(x);
                    if (x - v > 0) {
                        temp.add(x - v);
                    }
                }
                set = temp;
            }

        }
        return -1;
    }
}

