class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int max = 0;
        int n = nums.length;
        int m = queries.length;
        int L = 1000;
        for(int i=0;i<n;i++) {
            if(nums[i] == 0) continue;
            boolean[] v = new boolean[L+1];
            v[0] = true;
            for(int j=0;j<m;j++) {
                if(queries[j][0]>i || queries[j][1]<i) continue;
                for(int k=L;k>=queries[j][2];k--) {
                    if(v[k-queries[j][2]]) v[k] = true;
                }
                // System.out.println(String.format("%d %d %s",i,j,Arrays.toString(v)));
                if(v[nums[i]]) {
                    max = Math.max(j+1,max);
                    break;
                }
            }
            if(!v[nums[i]]) return -1;
        }
        return max;
    }
}

