class Solution {
public:
    struct Query {
    int index;
    int val;
    bool operator<(const Query& other) const {
        return index < other.index;
    }
};

int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    int m = queries.size();

    vector<vector<pair<char, Query>>> events(n + 1);
    for (int k = 0; k < m; ++k) {
        int li = queries[k][0];
        int ri = queries[k][1];
        int val = queries[k][2];
        if (li < 0) li = 0;
        if (ri >= n) ri = n - 1;
        if (li > ri) continue;
        Query q = {k, val};
        events[li].emplace_back('s', q);
        if (ri + 1 < n) {
            events[ri + 1].emplace_back('e', q);
        } else if (ri + 1 == n) {
            events[n].emplace_back('e', q);
        }
    }

    vector<vector<Query>> cover(n);
    set<Query> active;
    for (int i = 0; i < n; ++i) {
        for (auto& ev : events[i]) {
            if (ev.first == 's') {
                active.insert(ev.second);
            } else {
                active.erase(ev.second);
            }
        }
        cover[i].assign(active.begin(), active.end());
    }

    int max_k = 0;
    for (int i = 0; i < n; ++i) {
        if (nums[i] == 0) continue;
        const vector<Query>& cq = cover[i];
        if (cq.empty()) return -1;
        unordered_map<int, int> dp;
        dp[0] = -1;
        bool found = false;
        int current_k = -1;
        for (const Query& q : cq) {
            int v = q.val;
            int idx = q.index;
            unordered_map<int, int> tmp;
            for (const auto& entry : dp) {
                int s = entry.first;
                int max_idx = entry.second;
                int new_sum = s + v;
                int new_max = max(max_idx, idx);
                if (tmp.find(new_sum) == tmp.end() || new_max < tmp[new_sum]) {
                    tmp[new_sum] = new_max;
                }
            }
            for (const auto& entry : tmp) {
                int s = entry.first;
                int new_max = entry.second;
                if (dp.find(s) == dp.end() || new_max < dp[s]) {
                    dp[s] = new_max;
                }
            }
            if (dp.find(nums[i]) != dp.end()) {
                current_k = dp[nums[i]] + 1;
                found = true;
                break;
            }
        }
        if (!found) return -1;
        max_k = max(max_k, current_k);
    }
    return max_k;
}
};

