class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int count0 = 0, maxNum = 0;
        for (int i = 0; i < nums.length; i++) {
        	if (nums[i] == 0) {
        		count0++;
        	}
        	maxNum = Math.max(maxNum, nums[i]);
        }
        if (count0 == nums.length) {
        	return 0;
        }

        int[][] status = new int[nums.length][maxNum + 1];
        for (int i = 0; i < queries.length; i++) {
        	int l = queries[i][0], r = queries[i][1], val = queries[i][2];
        	for (int j = l; j <= r; j++) {
        		if (nums[j] == 0) {
        			continue;
        		}
        		for (int k = maxNum; k >= 0; k--) {
        			if (status[j][k] != 0 || k == 0) {
        				int next = k + val;
        				if (next <= maxNum) {
        					status[j][next] = 1;
        					if (next == nums[j]) {
        						nums[j] = 0;
        						count0++;
        						if (count0 == nums.length) {
        							return i + 1;
        						}
        					}
        				}
        			}
        		}
        	}
        }

        return -1;
    }
}

