class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        cover = [[] for _ in range(n)]
        for j, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                cover[i].append((j, val))

        for i in range(n):
            cover[i].sort(key=lambda x: x[0])

        max_indices = []
        for i in range(n):
            target = nums[i]
            if target == 0:
                max_indices.append(-1)
                continue
            dp = {0: -1}
            for (j, val) in cover[i]:
                new_dp = {}
                for x in list(dp.keys()):
                    new_x = x + val
                    new_max = max(dp[x], j)
                    if new_x in dp:
                        if new_max < dp[new_x]:
                            new_dp[new_x] = new_max
                    else:
                        new_dp[new_x] = new_max
                for x in new_dp:
                    if x not in dp or new_dp[x] < dp[x]:
                        dp[x] = new_dp[x]
            if target in dp:
                max_indices.append(dp[target])
            else:
                return -1

        max_k = max(max_indices)
        if max_k == -1:
            return 0
        else:
            return max_k + 1

