class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(queries)
        lo, hi = 0, len(queries)
        def validate(k):
            for idx, num in enumerate(nums):
                if num == 0:
                    continue
                dp = [False] * (num + 1)
                dp[0] = True
                for i in range(k):
                    l, r, v = queries[i]
                    if l <= idx <= r and v <= num:
                        for j in range(num, v - 1, -1):
                            if dp[j - v]:
                                dp[j] = True
                        # if dp[num]:
                        #     break
                if not dp[num]:
                    return False
            return True
        while lo <= hi:
            mi = (lo + hi) >> 1
            if validate(mi):
                hi = mi - 1
            else:
                lo = mi + 1

        if lo == n + 1:
            lo = -1
        return lo

