{
public:
  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    using bs = bitset<1001>;
    int m = Size(queries);
    int ans = 0;
    int n = Size(nums);
    for(int i = 0; i < n; i++) {
      bs dp;
      dp[0] = true;
      int minReq = m + 1;
      if(nums[i] == 0) {
        minReq = 0;
      } else {
        for(int j = 0; j < m; j++) {
          var &q = queries[j];
          if(q[0] <= i && i <= q[1]) {
            dp = dp | (dp << q[2]);
          }
          if(dp[nums[i]]) {
            minReq = j + 1;
            break;
          }
        }
        ans = max(ans, minReq);
      }
    }
    return ans > m ? -1 : ans;
  }
};
