class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        nq = len(queries)
        n = len(nums)
        if sum(nums) == 0: return 0
        # dp nums[i]  0 
        max_times = 0
        for i in range(n):
            times = self.check_times(i, nums[i], queries)
            # print(nums[i], times)
            if times == -1:
                return -1
            max_times = max(max_times, times)
        return max_times



    def check_times(self, idx: int, num: int, queries: List[List[int]]) -> int:
        if num == 0:
            return 0
        nq = len(queries)
        dp = [0 for _ in range(num+1)]
        dp[num] = 1
        for i in range(nq):
            l, r, val = queries[i]
            if l <= idx and r >= idx:
                for j in range(val, num+1):
                    if dp[j] == 1:
                        dp[j-val] = 1
            if dp[0] == 1:
                # print(dp)
                return i+1
        # print(dp)
        return -1

