class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if(*max_element(nums.begin(), nums.end()) == 0) return 0;
        int n = nums.size(), ans = 1e9; vector<int> ret;
        for(int i=0; i<n; i++) {
            int x = nums[i];
            bitset<1001> dp; dp[0] = 1;
            for(int k = 0; k < queries.size(); k++) {
                const auto& q = queries[k];
                if(q[0] <= i && i <= q[1]) {
                    dp |= dp << q[2];
                }
                if(dp[x]) { ret.push_back(k+1); break; }
            }
        }
        if(ret.size() != n) return -1;
        return *max_element(ret.begin(), ret.end());
    }
};

