type query struct {
	index int
	val   int
}

func minZeroArray(nums []int, queries [][]int) int {
	n := len(nums)
	maxK := 0

	for i := 0; i < n; i++ {
		target := nums[i]
		if target == 0 {
			continue
		}

		var qs []query
		for j, q := range queries {
			l, r := q[0], q[1]
			if l <= i && i <= r {
				qs = append(qs, query{index: j, val: q[2]})
			}
		}

		dp := make(map[int]int)
		dp[0] = -1
		minMaxJ := math.MaxInt32

		for _, q := range qs {
			j := q.index
			val := q.val

			newDp := make(map[int]int)
			for s, currentJ := range dp {
				if existingJ, ok := newDp[s]; !ok || currentJ < existingJ {
					newDp[s] = currentJ
				}

				newS := s + val
				newJ := max(currentJ, j)
				if existingJ, ok := newDp[newS]; !ok || newJ < existingJ {
					newDp[newS] = newJ
				}
			}

			dp = newDp

			if currentJ, ok := dp[target]; ok {
				if currentJ < minMaxJ {
					minMaxJ = currentJ
				}
			}
		}

		if minMaxJ == math.MaxInt32 {
			return -1
		}

		kI := minMaxJ + 1
		if kI > maxK {
			maxK = kI
		}
	}

	return maxK
}

