class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& Q) {

        const int m = Q.size();
        auto check = [&](int v, int x)->int{

            if(v == 0) {
                return 0;
            }

            bitset<1001>bs;
            bs[0] = 1;

            for(int i = 0;i < m;i++) {
                int l = Q[i][0], r = Q[i][1], val = Q[i][2];
                if(l <= x && x <= r) {
                    bs |= (bs << val);
                    if(bs[v] == 1) {
                        return i + 1;
                    }
                }    
            }

            return -1;
        };

        int ans = 0;
        for(int i = 0;i < a.size();i++) {
            auto t = check(a[i], i);
            if(t == -1) {
                return t;
            }
            ans = std::max(ans, t);
        }

        return ans;
    }
};

