                if (q[i][0] > j || q[i][1] < j) continue;
                for (int k = 0; k <= 1000; ++k) {
                    if (k - q[i][2] < 0) continue;
                    if (v[j][k] != 1) continue;
                    v[j][k - q[i][2]] = 1;
                    if (k - q[i][2] == 0) {
                        log[j] = min(log[j], i + 1);
                    }
                }
            }
        }
