class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),m=queries.size();
        auto check=[&](int k){
            vector<vector<int>> v(n);
            for(int i=0;i<k;i++){
                vector<int>&q=queries[i];
                int l=q[0],r=q[1],val=q[2];
                for(int i=l;i<=r;i++){
                    v[i].push_back(val);
                }
            }
            int s=0;
            for(int i=0;i<n;i++){
                if(!nums[i])continue;
                bitset<1001> dp;
                dp[0]=1;
                for(int& num:v[i]){
                    dp|=dp<<num;
                }
                if(!dp[nums[i]])return false;
            }
            return true;
        };
        int left=0,right=m;
        while(left<=right){
            int mid=(left+right)>>1;
            if(check(mid)){
                right=mid-1;
            }else{
                left=mid+1;
            }
        }
        if(left==m+1){
            return -1;
        }
        return left;
    }
};

