class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        // k
        int ans = Integer.MIN_VALUE;
        for (int i = 0; i < nums.length; i++) {
            int max = getMax(i, nums[i], queries);
            if (max != -1) {
                ans = Math.max(ans, max);
            } else {
                return -1;
            }
        }
        return ans;
    }

    private int getMax(int idx, int n, int[][] queries) {
        List<Integer> list = new ArrayList<>();
        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            if (l <= idx && r >= idx) {
                list.add(val);
            } else {
                list.add(-1);
            }
        }
        int[] nums = new int[list.size()];
        for (int i = 0; i < list.size(); i++) {
            nums[i] = list.get(i);
        }
        int minN = findMinN(nums, n);
        return minN;
    }

    public int findMinN(int[] nums, int target) {
        if (target == 0) {
            return 0;
        }
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == -1) {
                continue;
            }
            int num = nums[i];
            if (num > target) {
                continue;
            }
            for (int j = target; j >= num; j--) {
                if (dp[j - num]) {
                    dp[j] = true;
                }
            }
            if (dp[target]) {
                return i + 1;
            }
        }
        return -1;
    }
}

