class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        def canComplete(k):
            for i in range(n):
                target = nums[i]
                if target == 0:
                    continue
                dp = 1
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        dp |= (dp << v)
                        dp &= (1 << (target + 1)) - 1
                        if (dp >> target) & 1:
                            break
                if not ((dp >> target) & 1):
                    return False
            return True

        l, h = 0, m+1
        res = -1
        while l<h:
            mid = (l+h)//2
            if canComplete(mid):
                res = mid
                h = mid
            else:
                l = mid + 1
        if res == -1 or res > m:
            return -1
        return res

