class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<int> a(1);
        for(int i = 0; i < n; i++) {
            a.push_back(nums[i]);
        }
        int l = 0, r = m;

        auto check = [&](int mid) -> int {
            vector<vector<int>> s(n + 1);
            for(int i = 0; i < mid; i++) {
                int l = 1 + queries[i][0], r = 1 + queries[i][1], val = queries[i][2];
                for(int j = l; j <= r; j++) {
                    s[j].push_back(val);
                }
            }  
            for(int i = 1; i <= n; i++) {
                vector<int> dp(1e3 + 1);
                dp[0] = 1;
                for(int val : s[i]) {
                    for(int j = a[i]; j >= val; j--) {
                        dp[j] |= dp[j - val];
                    }
                }
                if(dp[a[i]] == 0) return 0;
            }
            return 1;
        };

        while(l <= r) {
            int mid = (l + r) >> 1;
            if(check(mid)) r = mid - 1;
            else l = mid + 1;
        }


        if(l == m + 1) return -1;
        return l;
    }
};

