class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = queries.length;
        int left = 0, right = n, ans = -1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (canMakeZero(nums, queries, mid)) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return ans;
    }

    private boolean canMakeZero(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        for (int j = 0; j < n; j++) {
            int target = nums[j];
            if (target == 0) continue;
            ArrayList<Integer> values = new ArrayList<>();
            int sumAll = 0;
            for (int i = 0; i < k; i++) {
                if (queries[i][0] <= j && j <= queries[i][1]) {
                    int val = queries[i][2];
                    values.add(val);
                    sumAll += val;
                }
            }

            if (sumAll < target) return false;
            if (!subsetSum(values, target)) return false;
        }
        return true;
    }

    private boolean subsetSum(List<Integer> values, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;
        for (int val : values) {
            for (int s = target - val; s >= 0; s--) {
                if (dp[s]) {
                    dp[s + val] = true;
                }
            }
            if (dp[target]) return true;
        }
        return dp[target];
    }
}

