class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<vector<int>> dp(n);
        for(int i = 0; i < n; i++){
            dp[i] = vector<int> (nums[i] + 1, 0);
            dp[i][0] = 1;
        }
        for(int i = 0; i <= m; i++){
            int t = 1;
            for(int j = 0; j < n; j++){
                if(dp[j][nums[j]] == 0){
                    t = 0;
                }
